#include <iostream>#include <vector>int main() {  int N;  std::cin >> N;  int a[N];  for (int i = 0; i < N; i++) {    std::cin >> a[i];  }  int dp[N], dp1, dp2;  dp[0] = 0;  dp[1] = abs(a[1] - a[0]);  for (int i = 2; i < N; i++) {    dp1 = dp[i - 2] + abs(a[i - 2] - a[i]);    dp2 = dp[i - 1] + abs(a[i - 1] - a[i]);    if (dp1 < dp2) {      dp[i] = dp1;    } else {      dp[i] = dp2;    }  }  std::cout << dp[N - 1] << std::endl;}