N = int(input())a_b_arr = []for _ in range(N-1):    a_b_arr.append(map(int,input().split()))res_arr = []N_arr = [ [ 0 for _ in range(N+1)] for _ in range(N+1) ]for i in a_b_arr:    a,b = i    for n,ai in enumerate(N_arr[a]):        if ai == 0:            a_min_col = n     for n, bi in enumerate(N_arr[b]):        if bi == 0:            b_min_col = n    this_color = a_min_col if b_min_col > a_min_col else b_min_col    N_arr[a][a_min_col] = 1    N_arr[b][b_min_col] = 1    res_arr.append( this_color )K = 0for ai in N_arr[a]:    if ai > K :        K = ai print(K)for i in res_arr:    print(i)