#include <cmath>#include <iostream>unsigned long change(unsigned long x, unsigned long k) {  unsigned long val, i;  val = 0;  i = 0;  while (x != 0) {    val += (x % 10) * pow(k, i);    x /= 10;    i++;  }  return val;}int main() {  unsigned long K;  unsigned long A, B;  std::cin >> K;  std::cin >> A >> B;  unsigned long a, b;  a = change(A, K);  b = change(B, K);  std::cout << a * b << std::endl;}