#include <bit>#include <cstdlib>#include <iostream>int main() {  int N;  std::cin >> N;  unsigned long X, A[N];  for (int i = 0; i < N; i++) {    std::cin >> A[i];  }  std::cin >> X;  unsigned long sumA = 0;  for (int i = 0; i < N; i++) {    sumA += A[i];  }  bool exit_flag = 0;  if (sumA == 0) {    exit_flag = 1;  }  unsigned long k = 0;  unsigned long sum = 0;  sum = (X / sumA) * sumA;  k = (X / sumA) * N;  while (1) {    if (exit_flag == 1) {      break;    }    for (int i = 0; i < N; i++) {      sum += A[i];      k++;      if (sum > X) {        std::cout << k << std::endl;        exit_flag = 1;        break;      }    }  }}