#include <atomic>#include <cstdlib>#include <iostream>#include <string>int main() {  std::string S, T;  std::cin >> S >> T;  int false_count = 0;  int index = 0;  for (int i = 0; i < int(S.length()); i++) {    if (S[i] != T[i]) {      false_count++;      index = size_t(i);    }  }  if (false_count == 0) {    std::cout << "Yes" << std::endl;  } else if (false_count != 2) {    std::cout << "No" << std::endl;  } else {    if (S[index - 1] == T[index] && S[index] == T[index - 1]) {      std::cout << "Yes" << std::endl;    } else {      std::cout << "No" << std::endl;    }  }}