#include <cmath>#include <iostream>int main() {  std::cout << "hoge" << std::endl;  unsigned long N = 0;  std::cin >> N;  std::cout << N << std::endl;  unsigned long ans[N] = {0};  unsigned long A, B;  unsigned long x, sum = 0;  for (unsigned long i = 0; i < N; i++) {    std::cin >> A >> B;    x = 0;    for (unsigned long j = A; j < B; j++) {      x += std::pow(N + 1, i);    }    sum += x;  }  for (unsigned long i = 0; i < N; i++) {    std::cout << ans[i] << std::endl;  }}